// now init it
try {

	// 'smt2_init_options' variable is passed from WP
	if ( typeof smt2_init_options !== 'undefined' ){

		// parse numeric values
		for ( var key  in  smt2_init_options ){
			if ( 
				
				smt2_init_options.hasOwnProperty( key )   
				&&   
				!isNaN( smt2_init_options[ key ] )
				&&
				smt2_init_options[ key ] !== ''

			){  // THEN parse:
				smt2_init_options[ key ] = parseInt( smt2_init_options[ key ] );
			}
		}

		// take care of a few details		
		if ( smt2_init_options.warnText ){ smt2_init_options.warn = true; }
		if ( smt2_init_options.disabled ){ smt2_init_options.disabled = Math.round(Math.random()); }
		smt2_init_options.contRecording = smt2_init_options.contRecording == 1  ?  true  :  false;



		// finally, init the recording with new settings:
		smt2.record( smt2_init_options );
		

	
	}else{
		smt2.record();
	}

} catch(err) {}